mapper.select:
select board_id, @rownum:=@rownum+1 as rownum, titl, cdt, vw_cnt, usr, is_del from bow_board, (select @rownum:=0) TMP
titl like #{pattern1} or conts like #{pattern1}
order by cdt desc, rownum desc
limit #{rowNo}, #{size}

mapper.update:
update bow_board 
set 
  titl = #{title},
  conts = #{contents} 
where board_id = #{no}

mapper.select:
select count(*) 
from 
  bow_board



    titl like #{pattern1}
    or conts like #{pattern1}
